import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

// Main App Component
const App = () => {
  // Base URL for the backend API
  const backendUrl = 'http://localhost:5000';

  // State variables for authentication and UI
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [username, setUsername] = useState(localStorage.getItem('username'));
  const [activePage, setActivePage] = useState('main-portfolio-page'); // 'main-portfolio-page' or 'appointment-list-page'

  // State variables for modals
  const [showRegistrationModal, setShowRegistrationModal] = useState(false);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [showAppointmentModal, setShowAppointmentModal] = useState(false);
  const [showAlertModal, setShowAlertModal] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [isConfirm, setIsConfirm] = useState(false);
  const [confirmAction, setConfirmAction] = useState(null); // Function to call on confirm

  // State variables for form messages
  const [regMessage, setRegMessage] = useState({ text: '', type: '' });
  const [loginMessage, setLoginMessage] = useState({ text: '', type: '' });
  const [appMessage, setAppMessage] = useState({ text: '', type: '' });

  // State for user appointments
  const [userAppointments, setUserAppointments] = useState([]);

  // Refs for forms to reset them
  const registrationFormRef = useRef(null);
  const loginFormRef = useRef(null);
  const appointmentFormRef = useRef(null);

  // Effect to update UI on token/username change
  useEffect(() => {
    // This effect runs whenever `token` or `username` state changes,
    // ensuring the UI (buttons, welcome message) is always in sync.
  }, [token, username]);

  // Effect to fetch appointments when the appointment list page is active and user is logged in
  useEffect(() => {
    if (activePage === 'appointment-list-page' && token) {
      fetchUserAppointments();
    }
  }, [activePage, token]); // Re-run if activePage or token changes

  // Function to show custom alert/confirm modal
  const customAlert = (message, isConfirmation = false, onConfirm = null) => {
    setAlertMessage(message);
    setIsConfirm(isConfirmation);
    <Dino></Dino>
    setConfirmAction(() => onConfirm);
    setShowAlertModal(true);
  };

  // Close all modals
  const closeAllModals = () => {
    setShowRegistrationModal(false);
    setShowLoginModal(false);
    setShowAppointmentModal(false);
    setShowAlertModal(false);
    setAlertMessage('');
    setIsConfirm(false);
    setConfirmAction(null);
  };

  // Handle page navigation
  const showPage = (pageId) => {
    setActivePage(pageId);
  };

  // Handle registration form submission
  const handleRegister = async (event) => {
    event.preventDefault();
    const formData = new FormData(registrationFormRef.current);
    const username = formData.get('username');
    const password = formData.get('password');

    try {
      const response = await axios.post(`${backendUrl}/api/register`, { username, password });
      setRegMessage({ text: response.data.message, type: 'success' });
      registrationFormRef.current.reset(); // Reset the form
      setTimeout(() => {
        closeAllModals();
        setShowLoginModal(true); // Open login modal after successful registration
      }, 1500);
    } catch (error) {
      setRegMessage({ text: error.response?.data?.message || 'Ошибка регистрации', type: 'error' });
    }
  };

  // Handle login form submission
  const handleLogin = async (event) => {
    event.preventDefault();
    const formData = new FormData(loginFormRef.current);
    const loginUsername = formData.get('username');
    const loginPassword = formData.get('password');

    try {
      const response = await axios.post(`${backendUrl}/api/login`, { username: loginUsername, password: loginPassword });
      setLoginMessage({ text: response.data.message, type: 'success' });
      setToken(response.data.accessToken);
      setUsername(response.data.user.username);
      localStorage.setItem('token', response.data.accessToken);
      localStorage.setItem('username', response.data.user.username);
      loginFormRef.current.reset(); // Reset the form
      setTimeout(closeAllModals, 1500);
    } catch (error) {
      setLoginMessage({ text: error.response?.data?.message || 'Ошибка входа', type: 'error' });
    }
  };

  // Handle logout
  const handleLogout = () => {
    setToken(null);
    setUsername(null);
    localStorage.removeItem('token');
    localStorage.removeItem('username');
    showPage('main-portfolio-page');
    setUserAppointments([]); // Clear appointments on logout
  };

  // Handle appointment form submission
  const handleAppointment = async (event) => {
    event.preventDefault();
    if (!token) {
      setAppMessage({ text: 'Для записи на приём необходимо войти в аккаунт.', type: 'error' });
      setTimeout(() => {
        closeAllModals();
        setShowLoginModal(true);
      }, 1500);
      return;
    }

    const formData = new FormData(appointmentFormRef.current);
    const name = formData.get('name');
    const email = formData.get('email');
    const phone = formData.get('phone');
    const date = formData.get('date');
    const time = formData.get('time');
    const service = formData.get('service');

    try {
      const response = await axios.post(`${backendUrl}/api/appointments`,
        { name, email, phone, date, time, service },
        { headers: { 'Authorization': `Bearer ${token}` } }
      );

      setAppMessage({ text: response.data.message, type: 'success' });
      appointmentFormRef.current.reset(); // Reset the form
      setTimeout(closeAllModals, 1500);
      if (activePage === 'appointment-list-page') {
        fetchUserAppointments(); // Refresh appointments if on that page
      }
    } catch (error) {
      setAppMessage({ text: error.response?.data?.message || 'Ошибка записи на приём', type: 'error' });
    }
  };

  // Fetch user's appointments
  const fetchUserAppointments = async () => {
    if (!token) {
      setUserAppointments([]);
      return;
    }
    try {
      const response = await axios.get(`${backendUrl}/api/user-appointments`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      setUserAppointments(response.data);
    } catch (error) {
      console.error('Error fetching appointments:', error);
      setUserAppointments([]); // Clear existing appointments on error
      // Use custom alert for user feedback
      customAlert('Не удалось загрузить ваши заявки. Попробуйте позже.');
    }
  };

  // Handle cancelling an appointment
  const handleCancelAppointment = (appointmentId) => {
    customAlert('Вы уверены, что хотите отменить эту заявку?', true, async () => {
      // This function will be called if the user confirms
      try {
        if (!token) {
          customAlert('Вы не авторизованы.');
          return;
        }

        const response = await axios.delete(`${backendUrl}/api/appointments/${appointmentId}`, {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        customAlert(response.data.message);
        fetchUserAppointments(); // Refresh the list
      } catch (error) {
        customAlert(error.response?.data?.message || 'Не удалось отменить заявку.');
      }
    });
  };

  // Component for modals (Registration, Login, Appointment, Alert/Confirm)
  const Modal = ({ show, onClose, title, children }) => {
    if (!show) return null;

    return (
      <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50 p-4 pt-[60px]">
        <div className="bg-[#333] m-auto p-8 border border-[#888] w-full max-w-lg rounded-lg relative text-white animate-fade-in-up">
          <button
            onClick={onClose}
            className="absolute top-2.5 right-5 text-[#aaa] hover:text-white text-3xl font-bold cursor-pointer"
          >
            &times;
          </button>
          <h2 className="text-center mb-5 text-4xl font-semibold">{title}</h2>
          {children}
        </div>
      </div>
    );
  };

  // Component for custom alert/confirm dialog
  const AlertModal = ({ show, message, isConfirm, onConfirm, onClose }) => {
    if (!show) return null;

    return (
      <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50 p-4 pt-[60px]">
        <div className="bg-[#333] m-auto p-8 border border-[#888] w-full max-w-sm rounded-lg relative text-white animate-fade-in-up text-center">
          <p className="text-lg mb-6">{message}</p>
          <div className="flex justify-center space-x-4">
            {isConfirm && (
              <button
                onClick={() => {
                  onConfirm && onConfirm();
                  onClose();
                }}
                className="bg-[#dc3545] hover:bg-[#c82333] text-white font-bold py-2 px-6 rounded-md transition duration-300 transform hover:scale-105"
              >
                Да
              </button>
            )}
            <button
              onClick={onClose}
              className={`${isConfirm ? 'bg-gray-300 hover:bg-gray-400 text-gray-800' : 'bg-[#007bff] hover:bg-[#0056b3] text-white'} font-bold py-2 px-6 rounded-md transition duration-300 transform hover:scale-105`}
            >
              {isConfirm ? 'Отмена' : 'ОК'}
            </button>
          </div>
        </div>
      </div>
    );
  };

  // Helper for redirecting external links
  const redirectTo = (url) => {
    window.open(url, '_blank');
  };

  return (
    <div
      className="w-full h-screen flex flex-col font-sans font-semibold text-white"
      style={{
        backgroundImage: 'url("static/photo_2024-11-27_19-32-45.jpg")',
        backgroundSize: '100%',
      }}
    >
      {/* Tailwind CSS Script (for JIT compilation in browser) */}
      <script src="https://cdn.tailwindcss.com"></script>
      <script dangerouslySetInnerHTML={{ __html: `
        tailwind.config = {
          theme: {
            extend: {
              fontFamily: {
                inter: ['Inter', 'sans-serif'],
              },
              keyframes: {
                'fade-in-up': {
                  '0%': { opacity: '0', transform: 'translateY(10px)' },
                  '100%': { opacity: '1', transform: 'translateY(0)' },
                },
              },
              animation: {
                'fade-in-up': 'fade-in-up 0.3s ease-out forwards',
              },
            },
          },
        };
      `}}></script>
      <style>
        @import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;600;700&display=swap');
        /* Custom styles that can't be easily replicated with Tailwind without extending config */
        /* or are better handled as direct styles/simple classes if they are very specific */
      </style>

      <div className="w-full h-full flex flex-col">
        <header className="text-left mb-5 ml-4">
          <h1 className="text-6xl m-0 font-bold uppercase">ПОЛИНА</h1>
          <div className="ml-[16%]">
            <h1 className="text-6xl m-0 font-bold uppercase">ПОДБОЛОТОВА</h1>
            <p className="inline-block mr-10 text-xl font-medium">копирайтер</p>
            <p className="inline-block mr-10 text-xl font-medium">делаю ярко</p>
            <p className="inline-block text-xl font-medium">качественно</p>
          </div>
          <div id="auth-status" className="absolute top-5 right-5 flex items-center gap-2.5">
            {token && username ? (
              <>
                <span id="usernameDisplay" className="text-lg font-semibold text-white">
                  Привет, <strong id="loggedInUsername">{username}</strong>!
                </span>
                <button id="logoutBtn" onClick={handleLogout} className="bg-white text-black border-none px-4 py-2 text-base cursor-pointer rounded transition duration-300 hover:bg-gray-200">Выйти</button>
                <button id="myAppointmentsBtn" onClick={() => showPage('appointment-list-page')} className="bg-white text-black border-none px-4 py-2 text-base cursor-pointer rounded transition duration-300 hover:bg-gray-200">Мои заявки</button>
              </>
            ) : (
              <button id="loginBtn" onClick={() => setShowLoginModal(true)} className="bg-white text-black border-none px-4 py-2 text-base cursor-pointer rounded transition duration-300 hover:bg-gray-200">Войти</button>
            )}
          </div>
        </header>

        {/* Main Portfolio Page */}
        <main id="main-portfolio-page" className={`page-content ${activePage === 'main-portfolio-page' ? 'active' : ''} grid grid-cols-1 md:grid-cols-2 gap-5 w-full h-full items-center`}>
          <div className="flex justify-start items-center font-semibold">
            <img src="static/photo_2024-11-27_19-32-45.jpg" alt="Полина Подболотова" className="mt-24 ml-4 w-[45%] h-[400px] shadow-xl"/>
          </div>
          <div className="flex justify-end items-center mt-24">
            <ul className="list-none p-0 m-0 max-w-lg">
              <li className="text-2xl mb-5 relative pl-5 leading-relaxed">
                <img src="https://raw.githubusercontent.com/lucide-icons/lucide/master/icons/arrow-up-right.svg" alt="Arrow icon" className="icon max-w-[30px] absolute left-0 top-1/2 -translate-y-1/2" />
                Более 2 лет опыта в копирайтинге для IT и маркетинга
              </li>
              <li className="text-2xl mb-5 relative pl-5 leading-relaxed">
                <img src="https://raw.githubusercontent.com/lucide-icons/lucide/master/icons/arrow-up-right.svg" alt="Arrow icon" className="icon max-w-[30px] absolute left-0 top-1/2 -translate-y-1/2" />
                Создаю тексты, которые продают
              </li>
              <li className="text-2xl mb-5 relative pl-5 leading-relaxed">
                <img src="https://raw.githubusercontent.com/lucide-icons/lucide/master/icons/arrow-up-right.svg" alt="Arrow icon" className="icon max-w-[30px] absolute left-0 top-1/2 -translate-y-1/2" />
                Увеличиваю продажи и узнаваемость брендов
              </li>
            </ul>
          </div>

          <section className="text-center my-12 px-5 col-span-full">
            <h2 className="text-6xl font-semibold mb-8 text-left">НАВЫКИ</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-5 justify-items-center items-center">
              <div className="skill-circle w-[600px] h-[600px] border border-white rounded-full flex items-center justify-center text-center text-4xl leading-snug">Анализ целевой аудитории</div>
              <div className="skill-circle w-[600px] h-[600px] border border-white rounded-full flex items-center justify-center text-center text-4xl leading-snug">SEO-оптимизация</div>
              <div className="skill-circle w-[600px] h-[600px] border border-white rounded-full flex items-center justify-center text-center text-4xl leading-snug">Знание<br/> английского языка<br/> на уровне B2</div>
              <div className="skill-circle w-[600px] h-[600px] border border-white rounded-full flex items-center justify-center text-center text-4xl leading-snug">Знание<br/> маркетинга</div>
              <div className="skill-circle w-[600px] h-[600px] border border-white rounded-full flex items-center justify-center text-center text-4xl leading-snug">Адаптация к<br/> различным форматам</div>
              <div className="skill-circle w-[600px] h-[600px] border border-white rounded-full flex items-center justify-center text-center text-4xl leading-snug">Знание<br/> немецкого языка <br/>на уровне B1</div>
            </div>
          </section>

          <section className="my-12 px-4 text-white col-span-full">
            <h2 className="font-semibold text-6xl mb-8 text-left">КОНТАКТЫ</h2>
            <div className="flex flex-col md:flex-row justify-between items-center mt-[300px] gap-8 md:gap-0">
              <div className="text-2xl leading-relaxed">
                <p className="m-0">pollyswamp@gmail.com</p>
                <p className="m-0">+7 927 831 88 55</p>
              </div>
              <div className="flex gap-2.5">
                <div className="contact-circle w-[180px] h-[180px] border border-white rounded-full flex items-center justify-center text-[2.3rem] cursor-pointer" onClick={() => redirectTo('https://t.me/polly_swamp')}>TG</div>
                <div className="contact-circle w-[180px] h-[180px] border border-white rounded-full flex items-center justify-center text-[2.3rem] cursor-pointer" onClick={() => redirectTo('https://vk.com/polly_swamp')}>VK</div>
                <div className="contact-circle w-[180px] h-[180px] border border-white rounded-full flex items-center justify-center text-[2.3rem] cursor-pointer" onClick={() => redirectTo('https://hh.ru/polly_swamp')}>HH</div>
              </div>
            </div>
            <div className="text-center mt-12">
              <button id="registerBtn" onClick={() => setShowRegistrationModal(true)} className="bg-white text-black border-none px-8 py-4 text-xl cursor-pointer rounded-md mx-2.5 transition duration-300 hover:bg-gray-200">Зарегистрироваться</button>
              <button id="appointmentBtn" onClick={() => setShowAppointmentModal(true)} className="bg-white text-black border-none px-8 py-4 text-xl cursor-pointer rounded-md mx-2.5 transition duration-300 hover:bg-gray-200">Записаться на приём</button>
            </div>
          </section>
        </main>

        {/* Appointment List Page */}
        <section id="appointment-list-page" className={`page-content ${activePage === 'appointment-list-page' ? 'active' : ''} flex-col px-5 box-border`}>
          <button id="backToMainBtn" onClick={() => showPage('main-portfolio-page')} className="bg-transparent text-white border border-white px-5 py-2.5 text-lg cursor-pointer rounded-md mb-5 ml-4 transition duration-300 hover:bg-white hover:text-black">← На главную</button>
          <h2 className="text-5xl mb-8 text-left ml-4">МОИ ЗАЯВКИ</h2>
          <div id="appointmentsList" className="grid grid-cols-1 md:grid-cols-auto-fit minmax(300px, 1fr) gap-5 px-4">
            {userAppointments.length === 0 ? (
              <p id="noAppointmentsMessage" className="text-center text-lg text-white">У вас пока нет активных заявок.</p>
            ) : (
              userAppointments.map(app => (
                <div key={app.id} className="bg-white bg-opacity-10 border border-white border-opacity-30 rounded-lg p-5 text-white relative shadow-md">
                  <p className="my-2 text-lg"><strong>Услуга:</strong> {app.service}</p>
                  <p className="my-2 text-lg"><strong>Дата:</strong> {app.date} в {app.time}</p>
                  <p className="my-2 text-lg"><strong>Имя:</strong> {app.name}</p>
                  <p className="my-2 text-lg"><strong>Email:</strong> {app.email}</p>
                  {app.phone && <p className="my-2 text-lg"><strong>Телефон:</strong> {app.phone}</p>}
                  <p className="my-2 text-lg"><strong>Статус:</strong> {app.status}</p>
                  <div className="mt-4 text-right">
                    <button
                      className="cancel-btn bg-red-600 hover:bg-red-700 text-white border-none px-4 py-2 rounded-md cursor-pointer text-sm ml-2.5 transition duration-300"
                      onClick={() => handleCancelAppointment(app.id)}
                    >
                      Отменить
                    </button>
                  </div>
                </div>
              ))
            )}
          </div>
        </section>

        {/* Registration Modal */}
        <Modal show={showRegistrationModal} onClose={closeAllModals} title="Регистрация">
          <form ref={registrationFormRef} onSubmit={handleRegister} className="flex flex-col">
            <label htmlFor="regUsername" className="mb-2 text-lg">Имя пользователя:</label>
            <input type="text" id="regUsername" name="username" required className="p-2.5 mb-4 border border-[#555] rounded bg-[#444] text-white text-base w-[calc(100%-22px)]"/>
            <label htmlFor="regPassword" className="mb-2 text-lg">Пароль:</label>
            <input type="password" id="regPassword" name="password" required className="p-2.5 mb-4 border border-[#555] rounded bg-[#444] text-white text-base w-[calc(100%-22px)]"/>
            <button type="submit" className="bg-[#007bff] hover:bg-[#0056b3] text-white px-5 py-3 border-none rounded-md cursor-pointer text-lg mt-2.5 transition duration-300">Зарегистрироваться</button>
            {regMessage.text && (
              <p className={`mt-4 text-center font-bold ${regMessage.type === 'success' ? 'text-green-500' : 'text-red-500'}`}>
                {regMessage.text}
              </p>
            )}
          </form>
        </Modal>

        {/* Login Modal */}
        <Modal show={showLoginModal} onClose={closeAllModals} title="Вход">
          <form ref={loginFormRef} onSubmit={handleLogin} className="flex flex-col">
            <label htmlFor="loginUsername" className="mb-2 text-lg">Имя пользователя:</label>
            <input type="text" id="loginUsername" name="username" required className="p-2.5 mb-4 border border-[#555] rounded bg-[#444] text-white text-base w-[calc(100%-22px)]"/>
            <label htmlFor="loginPassword" className="mb-2 text-lg">Пароль:</label>
            <input type="password" id="loginPassword" name="password" required className="p-2.5 mb-4 border border-[#555] rounded bg-[#444] text-white text-base w-[calc(100%-22px)]"/>
            <button type="submit" className="bg-[#007bff] hover:bg-[#0056b3] text-white px-5 py-3 border-none rounded-md cursor-pointer text-lg mt-2.5 transition duration-300">Войти</button>
            {loginMessage.text && (
              <p className={`mt-4 text-center font-bold ${loginMessage.type === 'success' ? 'text-green-500' : 'text-red-500'}`}>
                {loginMessage.text}
              </p>
            )}
          </form>
        </Modal>

        {/* Appointment Modal */}
        <Modal show={showAppointmentModal} onClose={closeAllModals} title="Запись на приём">
          <form ref={appointmentFormRef} onSubmit={handleAppointment} className="flex flex-col">
            <label htmlFor="appName" className="mb-2 text-lg">Ваше имя:</label>
            <input type="text" id="appName" name="name" required className="p-2.5 mb-4 border border-[#555] rounded bg-[#444] text-white text-base w-[calc(100%-22px)]"/>
            <label htmlFor="appEmail" className="mb-2 text-lg">Email:</label>
            <input type="email" id="appEmail" name="email" required className="p-2.5 mb-4 border border-[#555] rounded bg-[#444] text-white text-base w-[calc(100%-22px)]"/>
            <label htmlFor="appPhone" className="mb-2 text-lg">Телефон (необязательно):</label>
            <input type="tel" id="appPhone" name="phone" className="p-2.5 mb-4 border border-[#555] rounded bg-[#444] text-white text-base w-[calc(100%-22px)]"/>
            <label htmlFor="appDate" className="mb-2 text-lg">Дата:</label>
            <input type="date" id="appDate" name="date" required className="p-2.5 mb-4 border border-[#555] rounded bg-[#444] text-white text-base w-[calc(100%-22px)]"/>
            <label htmlFor="appTime" className="mb-2 text-lg">Время:</label>
            <input type="time" id="appTime" name="time" required className="p-2.5 mb-4 border border-[#555] rounded bg-[#444] text-white text-base w-[calc(100%-22px)]"/>
            <label htmlFor="appService" className="mb-2 text-lg">Услуга:</label>
            <select id="appService" name="service" required className="p-2.5 mb-4 border border-[#555] rounded bg-[#444] text-white text-base w-[calc(100%-22px)]">
              <option value="">Выберите услугу</option>
              <option value="Копирайтинг">Копирайтинг</option>
              <option value="Редактирование">Редактирование</option>
              <option value="SEO-оптимизация">SEO-оптимизация</option>
            </select>
            <button type="submit" className="bg-[#007bff] hover:bg-[#0056b3] text-white px-5 py-3 border-none rounded-md cursor-pointer text-lg mt-2.5 transition duration-300">Записаться</button>
            {appMessage.text && (
              <p className={`mt-4 text-center font-bold ${appMessage.type === 'success' ? 'text-green-500' : 'text-red-500'}`}>
                {appMessage.text}
              </p>
            )}
          </form>
        </Modal>

        {/* Custom Alert/Confirm Modal */}
        <AlertModal
          show={showAlertModal}
          message={alertMessage}
          isConfirm={isConfirm}
          onConfirm={confirmAction}
          onClose={closeAllModals}
        />
      </div>
    </div>
  );
};

export default App;
